def scoreCalc(eventType,slotNumber,user):
	if slotNumber < 4 :
		if user.profile.sleepChoice == 'C':
			return 0
		elif user.profile.sleepChoice == 'B':
			if slotNumber > 1:
				return 0
	eventindex = eventDict[eventType]
	if user.profile.studyChoice == '1':
		score = dayMat[eventindex][slotNumber]
	elif user.profile.studyChoice == '3':
		score = NightMat[eventindex][slotNumber]
	else :
		score = LateNightMat[eventindex][slotNumber]
	return score
